import React, {useEffect, useState } from 'react';
import Recipe from './Recipe';
import './App.scss';

const App = ()=> {

  const APP_ID = "51c42fd9"
  const APP_KEY = "34f6d70df594833bbe0b8c95951089b4"
  const APP_URL = `https://api.edamam.com/search?q=${query}`
  const APP_REQUEST = `${APP_URL}&app_id=${APP_ID}&app_key=${APP_KEY}`

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");
  const [query, setQuery] = useState("");

  useEffect(() => {
    getRecipes();
  }, [query]);

  const getRecipes = async () => {
    const response = await fetch(APP_REQUEST)
    const data = await response.json();
    setRecipes(data.hits);
  };

  const updateSearch = e => {
    setSearch(e.target.value);
    console.log(search);
  };

  const getQuery = e => {
    e.preventDefault();
    setQuery(search);
    setSearch("");
  }

  return(
    <div className="App">
      <form
        className="search__form"
        onSubmit={getQuery}
      >
        <input
          type="text"
          className="search__bar"
          value={search}
          onChange={updateSearch}
        />
        <button
          type=""
          className="search__button">
          Search
        </button>
      </form>
      <div className="recipes">
        {recipes.map(recipe => (
          <Recipe
            key={recipe.recipe.label}
            title={recipe.recipe.label}
            calories={recipe.recipe.calories}
            image={recipe.recipe.image}
            ingredients={recipe.recipe.ingredients}
          />
        ))}
      </div>
    </div>
  );
}

export default App;
