import React from 'react';

const Recipe = ({title, calories, image, ingredients}) => {
  const getImage = () => {
    const image = document.getElementsByClassName('card__image');
    console.log(image);
    const src = image.style.getAttribute('date-image-src');
    image.style.backgroundImage="url('" + src + "')";
  }
  return (
    <div className="card">
      <div className="row">
        <div className="col-8">
          Test
        </div>
        <div className="col-4">
          <div data-image-src={image} className="card__image">
            {getImage()}
          </div>
        </div>
      </div>
      <div className="card__text">
        <span className="date">4 days ago</span>
        <h2>{title}</h2>
        <ul>
          {ingredients.map(ingredient =>(
            <li>
              {ingredient.text}
            </li>
          ))}
        </ul>
      </div>
      <div className="card__data">
        {Math.round(calories)} calories
      </div>
    </div>
  );
}

export default Recipe;
