import React, {useEffect, useState } from 'react';
import Recipe from './Components/Recipe';
import './stylesheet/app.scss';

const App = ()=> {
  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");
  const [query, setQuery] = useState("banana");

  const APP_ID = "51c42fd9"
  const APP_KEY = "34f6d70df594833bbe0b8c95951089b4"
  const APP_URL = `https://api.edamam.com/search?q=`
  const APP_REQUEST = `${APP_URL}${query}&app_id=${APP_ID}&app_key=${APP_KEY}`

  useEffect(() => {
    getRecipes();
  }, [query]);

  const getRecipes = async () => {
    const response = await fetch(APP_REQUEST)
    const data = await response.json();
    setRecipes(data.hits);
  };

  const updateSearch = e => {
    setSearch(e.target.value);
  };

  const getQuery = e => {
    e.preventDefault();
    setQuery(search);
    setSearch("");
  }

  return(
    <div className="App">
      <form
        className="search__form"
        onSubmit={getQuery}
      >
        <input
          type="text"
          className="search__bar"
          value={search}
          onChange={updateSearch}
        />
        <button
          type=""
          className="search__button">
          Search
        </button>
      </form>
      <div className="container">
        <div className="row">
          <div className="col-6 recipes">
            {recipes.map(recipe => (
              <Recipe
                key={recipe.recipe.label}
                title={recipe.recipe.label}
                calories={recipe.recipe.calories}
                image={recipe.recipe.image}
                ingredients={recipe.recipe.ingredients}
                />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
