import React, {useEffect, useState } from 'react';
import Recipe from './Recipe';
import './App.css';

const App = ()=> {

  const APP_ID = "51c42fd9"
  const APP_KEY = "34f6d70df594833bbe0b8c95951089b4"
  const APP_URL = "https://api.edamam.com/search?q=chicken"
  const APP_REQUEST = `${APP_URL}&app_id=${APP_ID}&app_key=${APP_KEY}`

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    getRecepes();
  }, []);

  const getRecepes = async () => {
    const response = await fetch(APP_REQUEST)
    const data = await response.json();
    setRecipes(data.hits);
    console.log(data.hits)
  };

  const UpdateSearch = e => {
    setSearch(e.target.value);
    console.log(search);
  }

  return(
    <div className="App">
      <form className="search__form">
        <input
          type="text"
          className="search__bar"
          value={search}
          onChange={UpdateSearch()}
        />
        <button
          type=""
          className="search__button">
          Search
        </button>
      </form>
      {recipes.map(recipe => (
        <Recipe
          key={recipe.recipe.label}
          title={recipe.recipe.label}
          calories={recipe.recipe.calories}
          image={recipe.recipe.image}
        />
      ))}
    </div>
  );
}

export default App;
